import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import React, { useState } from "react";
import ExpensesFilter from "./ExpensesFilter";

export default function Expenses({ expenses }) {
  const [filterYear, setFilterYear] = useState("2020");
  const [filterYearText, setFilterYearText] = useState("2020, 2021 & 2022");

  function filterChangeHandler(selectedYear) {
    console.log(selectedYear);
    setFilterYear(selectedYear);

    if (selectedYear === "2019") {
      setFilterYearText("2020, 2021 & 2022");
    } else if (selectedYear === "2020") {
      setFilterYearText("2019 2021 & 2022");
    } else {
      setFilterYearText("2019, 2020 & 2021");
    }
  }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filterYear}
          onChangeFilter={filterChangeHandler}
        />
        <p>Data for years {filterYearText} is hidden.</p>
        {expenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
}
